def getMessage(rawStr, tolerance=100):
    frequencies = dict()
    for c in rawStr:
        frequencies[c] = frequencies.get(c, 0) + 1

    noiseChars = [c for c, freq in frequencies.items() if freq > tolerance]
    filterRegexp = re.compile('[{}]+'.format(''.join(noiseChars)))
    cleanStr = re.sub(filterRegexp, '', rawStr)
    return cleanStr

def doPost(url, **params):
    if url is None:
        raise ValueError('URL is empty so it doesn\'t get saved into the repo...')
    response = requests.post(url, data=params)
    return response.text

if __name__ == '__main__':
    print(getMessage(_RAW_STR))
    print(doPost(None, param1=10, param2='success'))
